        partial void APInvDtlDEASchBeforeUpdate()
        {
            if (ttAPInvDtlDEASch.DspAmortAmt < 0m || ttAPInvDtlDEASch.DocDspAmortAmt < 0m)
            {
                throw new BLException(Strings.AmortAmtMustBePositive);
