            int saveMscNum = 0;
            Guid rCurrentAPInvMscSysRowID = APIHAPInvMsc.SysRowID;
            bool afterTaxCalc = false;


            if (APInvHed != null && (APInvHed.ReadyToCalc && ReCalcTaxes))
            {
                saveVendorNum = APIHAPInvMsc.VendorNum;
                saveInvoiceNum = APIHAPInvMsc.InvoiceNum;
