                ttAPInvHed.FiscalCalendarID = CompanyFiscalCalendarID;
                ttAPInvHed.FiscalPeriod = 0;
                ttAPInvHed.FiscalYear = 0;
                ttAPInvHed.FiscalYearSuffix = "";
            }
            else
            {
                if (ttAPInvHed.TransApplyDate == null)
