            {
                throw new BLException(Strings.APInvHedRecordNotFound, "ttAPInvHed", "RowMod");
            }
            APInvHedAfterGetRows();
            CalculateCOIFRS_PV(ttAPInvHed.VendorNum, ttAPInvHed.InvoiceNum);

            ds = CurrentFullTableset;
        }

