                ttAPInvDtl.ScrLineDiscAmt = outScrLineDiscAmt2;
                ttAPInvDtl.Rpt1ScrLineDiscAmt = outRpt1ScrLineDiscAmt2;
                ttAPInvDtl.Rpt2ScrLineDiscAmt = outRpt2ScrLineDiscAmt2;
