                ttAPIHAPInvMsc.LCFlag = PurMisc.LCFlag;
                var outType2 = ttAPIHAPInvMsc.Type;
                var outPercentage2 = ttAPIHAPInvMsc.Percentage;
                var outScrDocMiscAmt5 = ttAPIHAPInvMsc.ScrDocMiscAmt;
