            var APInvPBQuery3 = DBExpressionCompiler.Compile(APInvPBExpression3);
            foreach (var ApInvPB_iterator in (APInvPBQuery3(Db, Session.CompanyID, ipVendNum, ipInvNum, ipInvLine, ipRoleCd)))
