                        {
                            ttLineTypes.DocTaxAmt = ttLineTypes.TaxMaxAmt * -1;

                        }

                    }
                    else
                    {
                        if (Math.Abs(ttLineTypes.DocTaxAmt) < ttLineTypes.TaxMinAmt && ttLineTypes.TaxMinAmt != 0)
                        {
                            if (ttLineTypes.DocTaxableAmt >= 0)
                            {
                                ttLineTypes.DocTaxAmt = ttLineTypes.TaxMinAmt;
