                    ttAPInvDtl.Rpt1LineDiscAmt = -ttAPInvDtl.Rpt1LineDiscAmt;
                    ttAPInvDtl.Rpt2LineDiscAmt = -ttAPInvDtl.Rpt2LineDiscAmt;
                    ttAPInvDtl.Rpt3LineDiscAmt = -ttAPInvDtl.Rpt3LineDiscAmt;
