                    ttAPInvMsc.Rpt3InMiscAmt = factor * ttAPInvMsc.Rpt3InMiscAmt;
                    if (inPrice == false)
                    {
                        ttAPInvMsc.ScrMiscAmt = debitMemoFactor * ttAPInvMsc.MiscAmt;
