        /// </summary>
        /// <param name="cpInvoiceNum">Invoice Number</param>
        /// <param name="opIsCorrectionInvoice">Is Correction Invoice</param>
        public void IsCorrectionInvoice(string cpInvoiceNum, out bool opIsCorrectionInvoice)
