                //bool schedExists = ExistsAPInvDtlDEASch(ttAPInvDtl.Company, ttAPInvDtl.VendorNum, ttAPInvDtl.InvoiceNum, ttAPInvDtl.InvoiceLine, posted: false);
                ttAPInvDtl.DEAStartDate = null;
