                    foreach (var realatedExp in SelectAPInvExpTaxWithLock(Session.CompanyID, apInvHedMscTax.VendorNum, apInvHedMscTax.InvoiceNum, "APInvHedMscTax", apInvHedMscTax.SysRowID))
                    {
